// JavaScript Document
	
 //二级菜单
 
$(function(){
	$(".allkinds").click(function(){
		if($(".second-menu").is(":visible")){
			$(".second-menu").hide();
			}
		else{
			$(".second-menu").show();
			}
		
		})
	})
	
	
$(function(){
	var divl=$(".floor_right");
	for(var i=0;i<divl.length;i++){
		var lilen=divl.eq(i).find("li");
		  for(var j=0;j<lilen.length;j++){
			  lilen.eq(2).css({"border-bottom":"none"});
			  lilen.eq(3).css({"border-bottom":"none"});
		  }
		}  
})


$(function(){
	var divl=$(".floor_midd");
	for(var i=0;i<divl.length;i++){
		var lilen=divl.eq(i).find("li");
		  for(var j=0;j<lilen.length;j++){
			  lilen.eq(0).css({"border-bottom":"1px solid #ededed"});
			  lilen.eq(1).css({"border-bottom":"1px solid #ededed"});
			  lilen.eq(2).css({"border":"none"});
		  }
		}  
})